mapper.select:
select b.basketId, b.userId, p.productName, p.price, p.defaultImage, b.basketQuantity
from basket b join product p on p.productId = b.productId
where b.userId=( select userId
                 from customer
                 where loginId=#{loginId}
                )

mapper.delete:
delete from basket
where userId=( select userId
               from customer
               where loginId=#{loginId}
             )

delete from basket
where userId=( select userId
               from customer
               where loginId=#{loginId}
             )
            and basketId=#{basketId}
